import React, {useState} from "react";
import Card from "../UI/Card.js";
import ExpenseItem from "./ExpenseItem";
import "./Expense.css"
import ExpneseFilter from "./ExpenseFilter.js";

function Expense(props) {    
      const [filterExpense, setFilterExpense] = useState('2022')
      const filterChangeHandler = filterExpense => {
        setFilterExpense(filterExpense);
        console.log(filterExpense)
      };

      

      return (
        <Card className = "expense"> 
          <ExpneseFilter selected = {filterExpense} onChangeFilter = {filterChangeHandler}/>

          {props.items.filter(expense => expense.date.getFullYear() ==filterExpense).map(expense => (
              <ExpenseItem 
              key = {expense.id} 
              title = {expense.title} 
              amount = {expense.amount} 
              date = {expense.date}/>
          ))}
        </Card>
      );
}


export default Expense
