import React, {useState} from "react";
import Card from "../UI/Card.js";
import ExpenseItem from "./ExpenseItem";
import "./Expense.css"
import ExpneseFilter from "./ExpenseFilter.js";

function Expense(props) {    
      const [filterExpense, setFilterExpense] = useState('All')
      const filterChangeHandler = filterExpense => {
        setFilterExpense(filterExpense);
      };
      const filterExpenseList = prop.items.filter(expense => {
        return expense.date.getFullYear().tostring() === filterExpense
      })

      const filterContent = <h2>No Expense Found</h2>
      if (filterExpenseList.length > 0) {
        filterContent = filterExpenseList.map(expense => (
          <ExpenseItem 
          key = {expense.id} 
          title = {expense.title} 
          amount = {expense.amount} 
          date = {expense.date}/>
      ))} else if(filterExpense === "All") {
        filterContent = props.items.map(expense=> {
          <ExpenseItem 
          key = {expense.id} 
          title = {expense.title} 
          amount = {expense.amount} 
          date = {expense.date}/>
        })
      }     

      return (
        <Card className = "expense"> 
          <ExpneseFilter selected = {filterExpense} onChangeFilter = {filterChangeHandler}/>

          {props.items.filter(expense => expense.date.getFullYear() ==filterExpense)}
        </Card>
      );
}


export default Expense
